INSERT INTO `doc_shb_gjj_policy` VALUES ('DocSHBGJJPolicy2017072422seq00000092', '北京外企人力资源服务安徽有限公司', '安徽', '六安市', '【病假工资规定】：-。', '【养老保险享受相关政策】：（一）、办理退休条件：
