INSERT INTO `doc_shb_gjj_policy` VALUES ('DocSHBGJJPolicy2017072422seq00000134', '成都市人力资源管理有限公司', '四川', '达州市', '【病假工资规定】：2016年病假工资最新规定
　　职工患病，医疗期内停工治疗在6个月以内的，其病假工资按以下办法计发：
