INSERT INTO `doc_shb_gjj_policy` VALUES ('DocSHBGJJPolicy2017072422seq00000258', '湖北华盛人力资源有限公司', '湖北', '京山市', '【病假工资规定】：用人单位支付病假工资不得低于本市最低工资标准的80％。
