INSERT INTO `doc_shb_gjj_policy` VALUES ('DocSHBGJJPolicy2017072422seq00000383', '吉林省外国企业服务有限公司', '', '通化市', '【病假工资规定】：第二十一条劳动者患病或者非因工负伤的，在病休期间，用人单位应当根据劳动合同或集体合同的约定支付病假工资。用人单位支付病假工资不得低于本市最低工资标准的80％。
