INSERT INTO `doc_shb_gjj_policy` VALUES ('DocSHBGJJPolicy2017072422seq00000411', '哈尔滨外国企业服务有限公司', '黑龙江', '双鸭山市', '', '【养老保险享受相关政策】：缴纳15年且到退休年龄，可以享受退休待遇。', '【失业保险处理流程】：单位及其个人按照规定参加失业保险1年以上并足额缴纳失业保险费的;

